mapper.select:
SELECT PRICE_DATE, AVG_PRICE
FROM AREA_AVG
WHERE OIL_CATEGORY = #{oilCategory}
  AND AREA_NAME = #{areaName}
ORDER BY PRICE_DATE ASC

SELECT PRICE_DATE, AVG_PRICE
FROM AREA_AVG
WHERE price_date >= DATE_SUB(CURDATE(), INTERVAL 1 YEAR)
  AND OIL_CATEGORY = #{oilCategory}
  AND AREA_NAME = #{areaName}
ORDER BY PRICE_DATE ASC

SELECT PRICE_DATE, AVG_PRICE
FROM AREA_AVG
WHERE price_date >= DATE_SUB(CURDATE(), INTERVAL 1 MONTH)
  AND OIL_CATEGORY = #{oilCategory}
  AND AREA_NAME = #{areaName}
ORDER BY PRICE_DATE ASC

SELECT PRICE_DATE, AVG_PRICE
FROM AREA_AVG
WHERE PRICE_DATE >= DATE_SUB(CURDATE(), INTERVAL 1 WEEK)
  AND OIL_CATEGORY = #{oilCategory}
  AND AREA_NAME = #{areaName}
ORDER BY PRICE_DATE ASC
